%% Drei Beispiele für den Satz von Marsaglia% Werden die Zufallszahlen eines LCG Zufallsgenerators in d Dimensionen als% d-Tupel aufgetragen, so liegen alle Punkte in max. ?_d(m·d!) Hyperebenen.global n; n = 100000 * 3;global s; s = 283475;% Die drei Beispieleexperiment(65539, 0, 2^31);experiment(6553, 8, 2^20);experiment(53, 12, 2^40); % Erst durch drehen der Ansicht ersichtlich% Funktion am Ende einer Datei hat lokalen Scope% Matlab rechnet bis 2^52 korrekt 2^52 +1 ~= 2^52function experiment(a,c,m)    global n; global s;    figure('Name', sprintf('a=%d, c=%d, m=%d', a, c, m));    zahlen = LCG(a, c, m, s, n);    scatter3(zahlen(1:3:n), zahlen(2:3:n), zahlen(3:3:n),'.');endfunction zufallszahlen = LCG(a, c, m, s, n)  schritt = @(x_n_minus_1)  mod( (a * x_n_minus_1 + c), m );  zufallszahlen = s;  for i = 1:n    zufallszahlen(i+1) = schritt(zufallszahlen(i));  endend